(function() {
  this.taxation = function() {
    var tax_categories;
    tax_categories = $('#product_tax_category_id').data('taxCategories');
    return tax_categories[$('#product_tax_category_id').val()];
  };

  this.incl_tax = function() {
    return taxation().incl_tax;
  };

  this.tax_rate = function() {
    return taxation().rate;
  };

  this.update_trade_price_with_tax = function() {
    var trade_price, trade_price_with_tax;
    trade_price = numbro().unformat($('#product_trade_price').val());
    trade_price_with_tax = trade_price * (100 + tax_rate()) / 100;
    return $('#product_trade_price_with_tax').val(numbro(trade_price_with_tax).format('0.00'));
  };

  this.update_markup_margin = function() {
    var margin_percent, markup_percent, retail_price, trade_price;
    trade_price = numbro().unformat(incl_tax() ? $('#product_trade_price_with_tax').val() : $('#product_trade_price').val());
    retail_price = numbro().unformat($('#product_retail_price').val());
    markup_percent = 100 * (retail_price - trade_price) / trade_price;
    margin_percent = 100 * (retail_price - trade_price) / retail_price;
    $('#markup_percent').val(numbro(markup_percent).format('0.00'));
    return $('#margin_percent').val(numbro(margin_percent).format('0.00'));
  };

}).call(this);
